func TestMarshalHasholdverHeader(t *testing.T) {
	v := Header{}
	binary.Read(rand.Reader, binary.BigEndian, &v)
	bts1, err := v.MarshalHasholdver()
	if err != nil {
		t.Fatal(err)
	}
	bts2, err := v.MarshalHasholdver()
	if err != nil {
		t.Fatal(err)
	}
	if !bytes.Equal(bts1, bts2) {
		t.Fatal("hash not stable")
	}
}

